function convertTimestamp(inputTimestamp) {
  const dateObj = new Date(inputTimestamp);
  const options = {
    weekday: 'short',
    day: '2-digit',
    month: 'short',
    year: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
    timeZone: 'GMT',
    timeZoneName: 'short'
  };
  return dateObj.toLocaleString('en-US', options);
}

const inputTimestamp = $('RSS Feed Trigger').first().json.pubDate;
const convertedTimestamp = convertTimestamp(inputTimestamp);

// Access the data from the AniList response for manga
const mangaData = $('HTTP Request - AniList API').first().json.data.Media;

// Format the start and end dates
const formatDate = (date) => {
  if (!date) return '';
  const { day, month, year } = date;
  return `${day ? day + ' ' : ''}${month ? new Date(0, month - 1).toLocaleString('en-US', { month: 'short' }) + ' ' : ''}${year || ''}`;
};

// List of possible fields with conditional checking for null or undefined values
const embedFields = [
  { name: 'Format', value: mangaData.format, inline: true },
  { name: 'Status', value: mangaData.status, inline: true },
  { name: 'Chapters', value: mangaData.chapters?.toString(), inline: true },
  { name: 'Volumes', value: mangaData.volumes?.toString(), inline: true },
  { name: 'Start Date', value: formatDate(mangaData.startDate), inline: true },
  { name: 'End Date', value: formatDate(mangaData.endDate), inline: true },
  { name: 'Genres', value: mangaData.genres?.join(', '), inline: true },
  { name: 'Average Score', value: mangaData.averageScore?.toString(), inline: true },
  { name: 'Studios', value: mangaData.studios.nodes.map(studio => studio.name).join(', '), inline: true }
].filter(field => field.value); // Filter out fields with null/undefined values

var body = {
  embeds: [
    {
      color: 3035554,
      author: {
        name: "Reisonantia's Recent Manga (AniList)",
        url: 'https://anilist.co/user/Reisonancia/',
        icon_url: 'https://raw.githubusercontent.com/LightCircleLLC/Eclipse/refs/heads/main/Assets/AniList/Avatars/Reisonancia.png',
      },
      title: mangaData.title.romaji, // Using Romaji title from AniList
      url: `https://anilist.co/manga/${mangaData.id}`, // URL to AniList manga page
      image: {
        url: mangaData.coverImage.extraLarge // Using extraLarge image from AniList
      },
      description: $('RSS Feed Trigger').first().json.title,
      fields: embedFields, // Only includes fields with valid values
      footer: {
        text: `AniList â€¢ ${convertedTimestamp}`,
        icon_url: 'https://anilist.co/img/icons/android-chrome-512x512.png',
      }
    }
  ]
};

return { body };

