function formatTeamStats(inputDescription) {
  // Break lines at specific points
  let formattedDescription = inputDescription.replace(/(\d{1,3}(?:,\d{3})*(?:\.\d+)?)/g, '**$1**');
  formattedDescription = formattedDescription.replace('Comprising ', '\nComprising ');
  formattedDescription = formattedDescription.replace('+ ', '\n+ ');

  return formattedDescription;
}

function extractNumberFromString(inputString) {
  const matches = inputString.match(/\d+/); // Matches the sequence of digits in the string
  return matches ? matches[0] : null; // Returns the first match or null if no digits found
}

const userStatsOdd8 = $('HTML').item.json.userStatsOdd[8]; // "Position in Country X"
const userStatsEven13 = $('HTML').item.json.userStatsEven[13]; // "Number of members Y"

const positionInCountry = extractNumberFromString(userStatsOdd8);
const numberOfMembers = extractNumberFromString(userStatsEven13);


function convertTimestamp(inputTimestamp) {
  const dateObj = new Date(inputTimestamp);
  const options = { weekday: 'short', day: '2-digit', month: 'short', year: 'numeric', hour: '2-digit', minute: '2-digit', second: '2-digit', timeZone: 'GMT', timeZoneName: 'short' };
  return dateObj.toLocaleString('en-US', options);
}

