function randomizeTheme(url) {
const min = 1;
const max = 40;
const randomTheme = Math.floor(Math.random() * (max - min + 1)) + min;

// check if URL already has a theme parameter
if (url.includes('theme=')) {
// replace existing theme value with randomTheme
return url.replace(/(theme=)\d+/, `$1${randomTheme}`);
} else {
// add new theme parameter to URL
return `${url}&theme=${randomTheme}`;
}
}

