const rssLink = $('RSS Feed Trigger').item.json.link;
const animeID = rssLink.match(/anime\/(\d+)/)[1];

return { animeID };

function convertTimestamp(inputTimestamp) {
  const dateObj = new Date(inputTimestamp);
  const options = { weekday: 'short', day: '2-digit', month: 'short', year: 'numeric', hour: '2-digit', minute: '2-digit', second: '2-digit', timeZone: 'GMT', timeZoneName: 'short' };
  return dateObj.toLocaleString('en-US', options);
}

const inputTimestamp = $('RSS Feed Trigger').item.json.pubDate;
const convertedTimestamp = convertTimestamp(inputTimestamp);


const animeData = $('HTTP Request - Jikan.moe API').item.json.data;

const embedFields = [
  { name: 'Type', value: animeData.type, inline: true },
  { name: 'Episodes', value: animeData.episodes.toString(), inline: true },
  { name: 'Status', value: animeData.status, inline: true },
  { name: 'Aired', value: animeData.aired.string, inline: true },
  { name: 'Premiered', value: `${animeData.season} ${animeData.year}`, inline: true },
  { name: 'Producers', value: animeData.producers.map(producer => `[${producer.name}](${producer.url})`).join(', '), inline: true },
  { name: 'Licensors', value: animeData.licensors.map(licensor => `[${licensor.name}](${licensor.url})`).join(', '), inline: true },
  { name: 'Studio', value: `[${animeData.studios[0].name}](${animeData.studios[0].url})`, inline: true },
  { name: 'Genre', value: animeData.genres.map(genre => `[${genre.name}](${genre.url})`).join(', '), inline: true },
  { name: 'Themes', value: animeData.themes.map(theme => `[${theme.name}](${theme.url})`).join(', '), inline: true },
  { name: 'Score', value: animeData.score.toString(), inline: true },
  { name: 'Ranked', value: `#${animeData.rank}`, inline: true },
  { name: 'Popularity', value: `#${animeData.popularity}`, inline: true },
  { name: 'Members', value: animeData.members.toString(), inline: true }
];

var body = {
  embeds: [
    {
      color: 3035554,
      author: {
        name: "Reisonantia's Recent Anime (MyAnimeList)",
        url: 'https://myanimelist.net/profile/Reisonantia',
        icon_url: 'https://raw.githubusercontent.com/LuminisNobilite/Eclipse/main/Assets/MyAnimeList/Avatars/Reisonantia.webp',
      },
      title: animeData.title,
      url: animeData.url,
      image: {
        url: animeData.images.jpg.large_image_url
      },
      description: $('RSS Feed Trigger').item.json.content,
      fields: embedFields,
      footer: {
        text: `MyAnimeList â€¢ ${convertedTimestamp}`,
        icon_url: 'https://raw.githubusercontent.com/LuminisNobilite/Eclipse/main/Assets/Icons/Guilded/Guilded%20-%20Gold%20MyAnimeList%20Icon%20v1.png',
      }
    }
  ]
};

return { body };
