const rssLink = $('RSS Feed Trigger').item.json.link;
const mangaID = rssLink.match(/manga\/(\d+)/)[1];

return { mangaID };

function convertTimestamp(inputTimestamp) {
  const dateObj = new Date(inputTimestamp);
  const options = {
    weekday: 'short',
    day: '2-digit',
    month: 'short',
    year: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
    timeZone: 'GMT',
    timeZoneName: 'short'
  };
  return dateObj.toLocaleString('en-US', options);
}

const inputTimestamp = $('RSS Feed Trigger').item.json.pubDate;
const convertedTimestamp = convertTimestamp(inputTimestamp);

const mangaData = $('HTTP Request - Jikan.moe API').item.json.data;

const embedFields = [
  { name: 'Type', value: mangaData.type || 'N/A', inline: true },
  { name: 'Volumes', value: mangaData.volumes ? mangaData.volumes.toString() : 'N/A', inline: true },
  { name: 'Chapters', value: mangaData.chapters ? mangaData.chapters.toString() : 'N/A', inline: true },
  { name: 'Status', value: mangaData.status || 'N/A', inline: true },
  { name: 'Published', value: mangaData.published.string || 'N/A', inline: true },
  { name: 'Genre', value: mangaData.genres.length ? mangaData.genres.map(genre => `[${genre.name}](${genre.url})`).join(', ') : 'N/A', inline: true },
  { name: 'Themes', value: mangaData.themes.length ? mangaData.themes.map(theme => `[${theme.name}](${theme.url})`).join(', ') : 'N/A', inline: true },
  { name: 'Serialization', value: mangaData.serializations.length ? mangaData.serializations.map(serialization => `[${serialization.name}](${serialization.url})`).join(', ') : 'N/A', inline: true },
  { name: 'Authors', value: mangaData.authors.length ? mangaData.authors.map(author => `[${author.name}](${author.url})`).join(', ') : 'N/A', inline: true },
  { name: 'Score', value: mangaData.score ? mangaData.score.toString() : 'N/A', inline: true },
  { name: 'Ranked', value: mangaData.rank ? `#${mangaData.rank}` : 'N/A', inline: true },
  { name: 'Popularity', value: mangaData.popularity ? `#${mangaData.popularity}` : 'N/A', inline: true },
  { name: 'Members', value: mangaData.members ? mangaData.members.toString() : 'N/A', inline: true },
  { name: 'Favorites', value: mangaData.favorites ? mangaData.favorites.toString() : 'N/A', inline: true }
];

var body = {
  embeds: [
    {
      color: 3035554,
      author: {
        name: "Reisonantia's Recent Manga (MyAnimeList)",
        url: 'https://myanimelist.net/profile/Reisonantia',
        icon_url: 'https://raw.githubusercontent.com/LuminisNobilite/Eclipse/main/Assets/MyAnimeList/Avatars/Reisonantia.webp',
      },
      title: mangaData.title || 'Unknown Title',
      url: mangaData.url,
      image: {
        url: mangaData.images.jpg.large_image_url
      },
      description: $('RSS Feed Trigger').item.json.content,
      fields: embedFields,
      footer: {
        text: `MyAnimeList â€¢ ${convertedTimestamp}`,
        icon_url: 'https://raw.githubusercontent.com/LuminisNobilite/Eclipse/main/Assets/Icons/Guilded/Guilded%20-%20Gold%20MyAnimeList%20Icon%20v1.png',
      }
    }
  ]
};

return { body };
