function convertTimestamp(inputTimestamp) {
  const dateObj = new Date(inputTimestamp);
  const options = {
    weekday: 'short',
    day: '2-digit',
    month: 'short',
    year: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
    timeZone: 'GMT',
    timeZoneName: 'short',
  };
  return dateObj.toLocaleString('en-US', options);
}


const inputTimestamp = $('RSS Feed Trigger').item.json.pubDate;
const convertedTimestamp = convertTimestamp(inputTimestamp);
const inputData = $('RSS Feed Trigger').item.json.content;
// Regex pattern to match the <img src> URL
const regex = /<img[^>]+src="([^">]+)"/g;
const imgSrcUrls = [];
let match;
// Extract all <img src> URLs using regex
while ((match = regex.exec(inputData)) !== null) {
  imgSrcUrls.push(match[1]);
}
const convertedUrls = imgSrcUrls.map((url, index) => {
  return url;
});


const maxEmbeds = 10; // Maximum number of embeds to send
const totalEmbeds = convertedUrls.length;
const numMessages = Math.ceil(totalEmbeds / maxEmbeds);
const payloads = [];


for (let i = 0; i < numMessages; i++) {
  const start = i * maxEmbeds;
  const end = start + maxEmbeds;
  const embeds = convertedUrls
    .slice(start, end)
    .map((url, index) => {
      const entryNumber = start + index + 1; // Entry number starts from 1
      let titleInit = $('RSS Feed Trigger').item.json.title;
      if (convertedUrls.length > 1) {
        titleInit = `${titleInit} [${entryNumber}]`;
      }
      return {
        color: 40191,
        author: {
          name: $('RSS Feed Trigger').item.json.author,
          url: 'https://www.pixiv.net/en/users/6662895',
          icon_url: 'https://pbs.twimg.com/profile_images/1201092432797954048/9wIdUsoF_400x400.jpg',
        },
        title: titleInit,
        url: $('RSS Feed Trigger').item.json.link,
        image: {
          url: url,
        },
        footer: {
          text: String.prototype.concat('Pixiv â€¢ ', convertedTimestamp),
          icon_url: 'https://i.imgur.com/jckoLce.png',
        },
      };
    });
  const body = { embeds };
  payloads.push({ body });
}


return { payloads };
