function convertTimestamp(inputTimestamp) {
  const dateObj = new Date(inputTimestamp);
  const options = {
    weekday: 'short',
    day: '2-digit',
    month: 'short',
    year: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
    timeZone: 'GMT',
    timeZoneName: 'short',
  };
  return dateObj.toLocaleString('en-US', options);
}

const inputTimestamp = $('RSS Feed Trigger').item.json.pubDate;
const convertedTimestamp = convertTimestamp(inputTimestamp);

const regexPattern = /@/i;
const creator = $('RSS Feed Trigger').item.json.creator;
const iconUrl = "https://raw.githubusercontent.com/LightCircleLLC/Eclipse/main/Assets/Twitter/Twitter%20Cosplay%20Feeds/Avatars/%40haneame_cos_400x400.jpg";
const inputContent = $('RSS Feed Trigger').item.json.content;
const regex = /<img\s+[^>]*src="([^"]+)"[^>]*>/g;
const match = regex.exec(inputContent);
const nameInit = `HaneAmeü¶ä „Éè„Éç„Ç¢„É°Èõ®Ê≥¢ (${creator})`;
const textInit = String.prototype.concat('Twitter ‚Ä¢ ', convertedTimestamp);

let descriptionHyperlinked = $('RSS Feed Trigger').item.json['title'];

// Fix the incorrect CDN URL pattern
function fixMalformedUrl(url) {
  // Correct the missing colon in 'https//'
  url = url.replace("https//", "https://");

  // Find and extract only the correct CDN URL
  const cdnUrlMatch = url.match(/https:\/\/cdn\.xcancel\.com[^\s"]+/);
  return cdnUrlMatch ? cdnUrlMatch[0] : url; // Return the correct part if found
}

// Use a regular expression to search for URLs within the description field
const urlRegex = /(https?:\/\/[^\s]+)/g;
descriptionHyperlinked = descriptionHyperlinked.replace(urlRegex, '[$1]($1)');

// Regular expression pattern to match hashtags
const hashtagRegex = /(^|\s)(#[^\s]+)/g;
const hashtags = descriptionHyperlinked.match(hashtagRegex);
if (hashtags) {
  hashtags.forEach(hashtag => {
    const cleanHashtag = hashtag.trim();
    const hashtagWithoutSharp = cleanHashtag.substring(1);
    const hashtagUrl = `https://www.twitter.com/hashtag/${hashtagWithoutSharp.toLowerCase()}`;
    const hashtagLink = `[${cleanHashtag}](${hashtagUrl})`;
    descriptionHyperlinked = descriptionHyperlinked.replace(cleanHashtag, hashtagLink);
  });
}

// Regular expression pattern to match @ mentions
const mentionRegex = /(^|\s)@(\w+)/g;
const mentions = descriptionHyperlinked.match(mentionRegex);
if (mentions) {
  mentions.forEach(mention => {
    const mentionWithoutSpace = mention.replace(/\s/g, '');
    const username = mentionWithoutSpace.substring(1);
    const mentionUrl = `https://twitter.com/${username}`;
    const mentionLink = ` [@${username}](${mentionUrl})`;
    descriptionHyperlinked = descriptionHyperlinked.replace(mention, mentionLink);
  });
}

if (match && match[1]) {
  // Fix the malformed URL and extract the correct CDN URL
  const imgSrc = fixMalformedUrl(match[1]);

  let embed = {
    color: 1942002,
    author: {
      name: nameInit,
      url: $("RSS Feed Trigger").item.json.link,
      icon_url: iconUrl,
    },
    url: $("RSS Feed Trigger").item.json['link'],
    image: {
      url: imgSrc,
    },
    footer: {
      text: textInit,
      icon_url: 'https://raw.githubusercontent.com/LightCircleLLC/Eclipse/main/Assets/Icons/Guilded/Guilded%20-%20Gold%20Twitter%20Icon%20v1.png',
    },
  };
  embed.description = descriptionHyperlinked;
  const body = {
    embeds: [embed],
  };
  return { body };
} else {
  let embed = {
    color: 1942002,
    author: {
      name: nameInit,
      url: $("RSS Feed Trigger").item.json.link,
      icon_url: iconUrl,
    },
    url: $("RSS Feed Trigger").item.json['link'],
    footer: {
      text: textInit,
      icon_url: 'https://raw.githubusercontent.com/LightCircleLLC/Eclipse/main/Assets/Icons/Guilded/Guilded%20-%20Gold%20Twitter%20Icon%20v1.png',
    },
  };
  embed.description = descriptionHyperlinked;
  const body = {
    embeds: [embed],
  };
  return { body };
}
