const regexPattern = /@/i;
const creator = $('RSS Feed Trigger').item.json.creator;
const iconUrl = "https://unavatar.io/twitter/PGR_GLOBAL?fallback=https://raw.githubusercontent.com/LuminisNobilite/Eclipse/main/Assets/Twitter/Twitter%20Gaming%20Feeds/Avatars/%40PGR_GLOBAL_400x400.jpg";
const inputContent = $('RSS Feed Trigger').item.json.content;
const regex = /<img\s+[^>]*src="([^"]+)"[^>]*>/g;
const match = regex.exec(inputContent);
const nameInit = `Punishing: Gray Raven (${creator})`;
const textInit = `Twitter â€¢ ${$('RSS Feed Trigger').item.json.pubDate}`;

let descriptionHyperlinked = $('RSS Feed Trigger').item.json['title'];
// Use a regular expression to search for URLs within the description field
const urlRegex = /(https?:\/\/[^\s]+)/g;
descriptionHyperlinked = descriptionHyperlinked.replace(urlRegex, '[$1]($1)');
// Regular expression pattern to match hashtags
const hashtagRegex = /(^|\s)(#[^\s]+)/g;
// Find all the hashtags in the description string
const hashtags = descriptionHyperlinked.match(hashtagRegex);
// Replace the hashtags with URLs
if (hashtags) {
  hashtags.forEach(hashtag => {
    const cleanHashtag = hashtag.trim(); // Trim spaces at the start and end
    const hashtagWithoutSharp = cleanHashtag.substring(1); // Remove the '#' symbol
    const hashtagUrl = `https://www.twitter.com/hashtag/${hashtagWithoutSharp.toLowerCase()}`;
    const hashtagLink = `[${cleanHashtag}](${hashtagUrl})`; // No extra spaces inside the brackets
    descriptionHyperlinked = descriptionHyperlinked.replace(cleanHashtag, hashtagLink);
  });
}

// Regular expression pattern to match @ mentions
const mentionRegex = /(^|\s)@(\w+)/g;
// Find all the @ mentions in the description string
const mentions = descriptionHyperlinked.match(mentionRegex);
// Replace the @ mentions with URLs
if (mentions) {
  mentions.forEach(mention => {
    const mentionWithoutSpace = mention.replace(/\s/g, ''); // Remove any spaces before the mention
    const username = mentionWithoutSpace.substring(1);
    const mentionUrl = `https://twitter.com/${username}`;
    const mentionLink = ` [@${username}](${mentionUrl})`; // Include the '@' symbol
    descriptionHyperlinked = descriptionHyperlinked.replace(mention, mentionLink);
  });
}


if (match && match[1]) {
  const imgSrc = match[1];
  let embed = {
    color: 1942002,
    author: {
      name: nameInit,
      url: $("RSS Feed Trigger").item.json.link,
      icon_url: iconUrl,
    },
    url: $("RSS Feed Trigger").item.json['link'],
    image: {
      url: imgSrc,
    },
    footer: {
      text: textInit,
      icon_url: 'https://raw.githubusercontent.com/LuminisNobilite/Eclipse/main/Assets/Icons/Guilded/Guilded%20-%20Gold%20Twitter%20Icon%20v1.png',
    },
  };
    embed.description = descriptionHyperlinked;
  const body = {
    embeds: [embed],
  };
  return { body };
} else {
  let embed = {
     color: 1942002,
    author: {
      name: nameInit,
      url: $("RSS Feed Trigger").item.json.link,
      icon_url: iconUrl,
    },
    url: $("RSS Feed Trigger").item.json['link'],
    footer: {
      text: textInit,
      icon_url: 'https://raw.githubusercontent.com/LuminisNobilite/Eclipse/main/Assets/Icons/Guilded/Guilded%20-%20Gold%20Twitter%20Icon%20v1.png',
    },
  };
  embed.description = descriptionHyperlinked;
  const body = {
    embeds: [embed],
  };
  return { body };
}
