function convertTimestamp(inputTimestamp) {
  const dateObj = new Date(inputTimestamp);
  const options = { weekday: 'short', day: '2-digit', month: 'short', year: 'numeric', hour: '2-digit', minute: '2-digit', second: '2-digit', timeZone: 'GMT', timeZoneName: 'short' };
  return dateObj.toLocaleString('en-US', options);
}

const inputTimestamp = $('RSS Feed Trigger').item.json.pubDate;
const convertedTimestamp = convertTimestamp(inputTimestamp);

const inputData = $('RSS Feed Trigger').item.json.content;

// Regex pattern to match the <img src> URL
const regex = /<img[^>]+src="([^">]+)"/;

// Extract the <img src> URL using regex
const match = inputData.match(regex);
const imgSrcUrl = match ? match[1] : '';

// Convert the URL according to the given constraints
const convertedUrlInit = imgSrcUrl.replace(/\/data\/preview\/([A-Za-z0-9]+)\/([A-Za-z0-9]+)\/([A-Za-z0-9]+\.[A-Za-z0-9]+)$/, '/image/$3');

const convertedUrlFinal = convertedUrlInit.replace("assets","files");


var body = {
  embeds: [
    {
      color: 14837107,
      author: {
        name: 'Blue Archive',
        url: 'https://bluearchive.nexon.com/home',
        icon_url:
          'https://pbs.twimg.com/profile_images/1509908462553100294/qKzWCkVR_400x400.png',
      },
      title: $('RSS Feed Trigger').item.json.title,
      url: $('RSS Feed Trigger').item.json.link,
      image: {
        url: convertedUrlFinal,
      },
      footer: {
        text: String.prototype.concat("Yande.re • ", convertedTimestamp, " • ", $('RSS Feed Trigger').item.json.author),
        icon_url: 'https://cdn.myanimelist.net/images/characters/8/269729.jpg',
      }
    }
  ]
};

return { body };
