function convertTimestamp(inputTimestamp) {
  const dateObj = new Date(inputTimestamp);
  const options = { weekday: 'short', day: '2-digit', month: 'short', year: 'numeric', hour: '2-digit', minute: '2-digit', second: '2-digit', timeZone: 'GMT', timeZoneName: 'short' };
  return dateObj.toLocaleString('en-US', options);
}

const iconUrlFinal = 'https://raw.githubusercontent.com/LightCircleLLC/Eclipse/refs/heads/main/Assets/Twitter/Twitter%20Gaming%20Feeds/Avatars/%40PGR_GLOBAL_400x400.jpg';
const nameInit = $('RSS Feed Trigger').first().json['author'];

const snippet = $('Set').first().json.data[0].snippet;

let thumbnailUrl;

if (snippet.thumbnails.maxres && snippet.thumbnails.maxres.url) {
  thumbnailUrl = snippet.thumbnails.maxres.url;
} else if (snippet.thumbnails.high && snippet.thumbnails.high.url) {
  thumbnailUrl = snippet.thumbnails.high.url;
} else if (snippet.thumbnails.medium && snippet.thumbnails.medium.url) {
  thumbnailUrl = snippet.thumbnails.medium.url;
} else if (snippet.thumbnails.default && snippet.thumbnails.default.url) {
  thumbnailUrl = snippet.thumbnails.default.url;
} else {
  // Handle the case when no suitable thumbnail is available
  thumbnailUrl = 'https://images5.alphacoders.com/123/1235223.jpg'; // Custom URL
}


const inputTimestamp = $('RSS Feed Trigger').first().json.pubDate;
const convertedTimestamp = convertTimestamp(inputTimestamp);

const textMod = String.prototype.concat("Youtube â€¢ ", convertedTimestamp);

let descriptionHyperlinked = $('Filter Code').first().json['media:group']['media:description'];

// Use a regular expression to search for URLs within the description field
const urlRegex = /(https?:\/\/[^\s]+)/g;
descriptionHyperlinked = descriptionHyperlinked.replace(urlRegex, '[$1]($1)');

// Regular expression pattern to match hashtags
const hashtagRegex = /(^|\s)(#[^\s]+)/g;
// Find all the hashtags in the description string
const hashtags = descriptionHyperlinked.match(hashtagRegex);
// Replace the hashtags with URLs
if (hashtags) {
  hashtags.forEach(hashtag => {
    const cleanHashtag = hashtag.trim(); // Remove leading and trailing spaces
    const hashtagUrl = `https://www.youtube.com/hashtag/${cleanHashtag.toLowerCase().replace('#', '')}`;
    const hashtagLink = `[${cleanHashtag}](${hashtagUrl})`; // Use cleaned hashtag
    // Check if there's a closing square bracket ']' after the hashtag, and if not, add a space
    if (!/\]\([^)]*$/.test(descriptionHyperlinked)) {
      descriptionHyperlinked = descriptionHyperlinked.replace(hashtag, `${hashtagLink} `); // Insert space after the link
    } else {
      descriptionHyperlinked = descriptionHyperlinked.replace(hashtag, `${hashtagLink}`);
    }
  });
}

// Regular expression pattern to match @ mentions
const mentionRegex = /(^|\s)@(\w+)/g;
// Find all the @ mentions in the description string
const mentions = descriptionHyperlinked.match(mentionRegex);
// Replace the @ mentions with URLs
if (mentions) {
  mentions.forEach(mention => {
    const username = mention.trim().substring(1); // Remove leading '@' and trim spaces
    const mentionUrl = `https://youtube.com/${username}`;
    const mentionLink = `[@${username}](${mentionUrl})`; // Include the '@' symbol
    descriptionHyperlinked = descriptionHyperlinked.replace(mention, mentionLink);
  });
}

let embed = {
    color: 16711680,
    author: {
      name: nameInit,
      url: 'https://www.youtube.com/@PunishingGrayRaven',
      icon_url: iconUrlFinal,
    },
    url: $("RSS Feed Trigger").first().json['link'],
    image: {
      url: thumbnailUrl,
    },
    footer: {
      text: textMod,
      icon_url: 'https://raw.githubusercontent.com/LightCircleLLC/Eclipse/refs/heads/main/Assets/Icons/Guilded/Guilded%20-%20Gold%20Youtube%20Icon%20v1.png',
    },
  };
    embed.title = $('RSS Feed Trigger').first().json['title'];
    embed.description = descriptionHyperlinked;
  const body = {
    embeds: [embed],
  };
  return { body };
